def transcript_to_context(transcript: dict) -> str:
    frases = []
    for seg in transcript.get("segments", []):
        text = seg.get("text", "").strip()

        if len(text) < 5:
            continue
        if "[" in text:
            continue

        frases.append(text)

    texto = " ".join(frases[:12])  # limita contexto

    return f"""
Transcrição do vídeo (fala natural, gírias brasileiras):

{texto}
"""



def build_prompt(contexto: str) -> str:
    return f"""
Você é um EDITOR SÊNIOR de vídeos virais brasileiros
especializado em TikTok e YouTube Shorts.

Sua missão é criar TEXTO QUE FAÇA A PESSOA PARAR DE SCROLLAR.

Gere um JSON EXATAMENTE neste formato:
{{
  "descricao": "1 frase curta e curiosa (máx. 90 caracteres)",
  "legenda": "1 a 2 frases com gancho forte, linguagem de rua",
  "hashtags": ["#hashtag1", "#hashtag2", "#hashtag3"]
}}

REGRAS OBRIGATÓRIAS:
- Português do Brasil
- Linguagem informal, natural, conversa real
- NÃO explique o contexto completo
- NÃO entregue o final da história
- Gere curiosidade ou estranhamento
- Texto simples, impacto alto
- Pense em comentários e retenção

BOAS PRÁTICAS (use se fizer sentido):
- Use termos como: "mano", "cara", "do nada", "ninguém esperava"
- Pode usar pergunta retórica
- Pode criar sensação de descoberta ou surpresa

HASHTAGS:
- Máx. 5
- Relacionadas ao conteúdo
- Evite hashtags genéricas (#fyp, #viral)

Transcrição do vídeo:
{contexto}

Responda SOMENTE com o JSON.
Não escreva nenhuma explicação fora do JSON.
"""

import subprocess
import json
import re

def score_segment(text: str) -> int:
    score = 0
    t = text.lower()

    # tamanho bom pra fala humana
    if 8 <= len(t) <= 120:
        score += 1

    # frases completas
    if t.endswith((".", "?", "!")):
        score += 1

    # emoção / reação
    if any(x in t for x in ["mano", "cara", "nossa", "rapaz", "caraca"]):
        score += 1

    # ação / decisão
    if any(x in t for x in ["vou", "fui", "fazer", "buscar", "chegar", "comprar"]):
        score += 1

    # pergunta gera retenção
    if "?" in t:
        score += 2

    # primeira pessoa conecta
    if re.search(r"\b(eu|meu|minha|a gente)\b", t):
        score += 1

    return score

def extract_json(text: str) -> dict:
    """
    Extrai o primeiro bloco JSON válido de um texto.
    """
    match = re.search(r"\{[\s\S]*\}", text)
    if not match:
        raise ValueError("Nenhum JSON encontrado na resposta")

    json_str = match.group(0)
    return json.loads(json_str)


def gerar_metadata_viral(transcript: dict) -> dict:
    contexto = transcript_to_context(transcript)
    prompt = build_prompt(contexto)

    process = subprocess.Popen(
        ["ollama", "run", "llama3"],
        stdin=subprocess.PIPE,
        stdout=subprocess.PIPE,
        stderr=subprocess.PIPE,
        text=True,
        encoding="utf-8"
    )

    stdout, stderr = process.communicate(prompt)

    if process.returncode != 0:
        raise RuntimeError(stderr)

    try:
        return extract_json(stdout)
    except Exception as e:
        return {
            "erro": str(e),
            "raw": stdout
        }





